DROP TRIGGER IF EXISTS `generate_unique_value`;
DELIMITER ;;
CREATE TRIGGER `generate_unique_value` BEFORE INSERT ON `edu_subject` FOR EACH ROW BEGIN
    DECLARE new_value INT;
    SELECT MAX(sort) + 1 INTO new_value FROM edu_subject;
    IF new_value IS NULL THEN
        SET new_value = 1; -- 如果表中没有数据，则初始值为1
    END IF;
    SET NEW.sort = new_value;
END
;;
